import React, {FC, useState} from 'react';

import cl from './MyForm.module.css';
import MyButton from '../MyButton/MyButton';
import { modalPropetiesType, modalTypes } from '../../../types/modalType';
import { useDispatch } from 'react-redux';
import { useTypedSelector } from '../../../hooks/useTypeSelector';
import { NotesActionsTypes } from '../../../types/notesTypes';
import { getAllNotes } from '../../../services/noteService';

interface MyFormProps {
    modalProperties: modalPropetiesType
}

const MyForm: FC<MyFormProps> = ({
    modalProperties
}) => {

    const dispatch = useDispatch();
    const {notes} = useTypedSelector(state => state.note);

    let onClickFunction = () => {
        const year = new Date().getFullYear();
        const month = new Date().getMonth() + 1;
        const day = new Date().getDate();

        const newId = Math.max(...getAllNotes(notes).map(note => note.id)) + 1;
    
        dispatch({
          type: NotesActionsTypes.ADD_NOTE,
          payload: { 
              id: newId,
              createdTime: `${year}-${month}-${day}`,
              content: noteContent,
              category: noteCategory,
              archived: false,
          }
        });

        modalProperties.setVisible(false);
        setNoteContent('');
    };

    if (modalProperties.method === modalTypes.EDIT){
        onClickFunction = () => {
            const note = getAllNotes(notes).find(note => note.id === modalProperties.index);
            if (note !== undefined){
                setNoteContent(note.content);
                setNoteCategory(note.category);
            }

            dispatch({
              type: NotesActionsTypes.EDIT_NOTE,
              payload: { 
                  id: modalProperties.index,
                  content: noteContent,
                  category: noteCategory,
              }
            });
    
            modalProperties.setVisible(false);
            setNoteContent('');
        };
    }

    const [noteContent, setNoteContent] = useState('');
    const [noteCategory, setNoteCategory] = useState('Task'); 

  return (
    <div className={cl.myForm}>
        <div className={cl.row}>
            <label htmlFor="noteContent">Note Content:</label>
            <textarea id="noteContent" rows={4} cols={50} 
                value={noteContent} onChange={e => setNoteContent(e.target.value)}></textarea>
        
        </div>

        <div className={cl.row}>
            <label htmlFor="noteCategory">Note Category:</label>
            <select id="noteCategory" value={noteCategory} 
                    onChange={e => setNoteCategory(e.target.value)}
            >
                <option value="Task">Task</option>
                <option value="Random Thought">Random Thought</option>
                <option value="Idea">Idea</option>
            </select>
        </div>

        <div className={cl.row}>
            <MyButton onClickFunction={onClickFunction}>Save Note</MyButton>
        </div>
    </div>
  );
}


export default MyForm;
