import { Category } from "./categoryTypes";

export enum NotesActionsTypes {
    ADD_NOTE = 'ADD_NOTE',
    DELETE_NOTE = 'DELETE_NOTE',
    EDIT_NOTE = 'EDIT_NOTE',
    ARCHIVE_OR_UNARCHIVE_NOTE = 'ARCHIVE_OR_UNARCHIVE_NOTE',
    
}

export interface NotesState {
    notes: any[];
}

export interface NoteType {
    id: number;
    createdTime: string;
    content: string;
    category: Category;
    archived: boolean;
}

interface NotesActionsDeleteOrArchive {
    type: NotesActionsTypes.DELETE_NOTE | NotesActionsTypes.ARCHIVE_OR_UNARCHIVE_NOTE;
    payload: number;
}

interface NotesActionsAdd {
    type: NotesActionsTypes.ADD_NOTE;
    payload: {
        createdTime: string;
        content: string;
        category: Category;
        archived: boolean;
    };
}

interface NotesActionsEdit {
    type: NotesActionsTypes.EDIT_NOTE;
    payload: NoteType,
    
}

export type NotesAction = NotesActionsAdd | NotesActionsEdit | NotesActionsDeleteOrArchive;
