import React, { useEffect } from 'react';
import './styles/App.css';
import Table from './components/Table';
import MyButton from './components/UI/MyButton/MyButton';
import MyModal from './components/UI/MyModal/MyModal';
import { modalPropetiesType, modalTypes } from './types/modalType';
import { getActiveNotes, getArchiveNotes } from './services/noteService';
import { useTypedSelector } from './hooks/useTypeSelector';
import { getInfoAboutCategoryes } from './services/categoryService';
import { Category, CategoryType } from './types/categoryTypes';
import { useActions } from './hooks/useActions';
import { NoteType } from './types/notesTypes';



function App() {
  const {notes, loadingNotes} = useTypedSelector(state => state.note);
  const {notesStats, loadingNotesStats} = useTypedSelector(state => state.notesStats)

  const activeNotes = getActiveNotes(notes);
  const archivedNotes = getArchiveNotes(notes);

  const {fetchNotes} = useActions();
  const {fetchNotesStats} = useActions();

  useEffect(() => {
    fetchNotes();
    fetchNotesStats();
  }, [])


  const [modalProperties, setModalProperties] = React.useState({
    visible: false,
    modalType: modalTypes.ADD,

    index: -1,
    oldNoteContent: '',
    oldNoteCategory: Category.TASK,
  })

  const createTable = (data: CategoryType | NoteType, extraClasses:string,
                       isCategory: boolean, setModalProperties : Function, loading : boolean) => {
    return (
      <Table data={activeNotes} 
      extraClasses='active' 
      isCategory = {false} 
      setModalProperties={setModalProperties} 
      loading={loadingNotes}>Active list</Table>
    )
  }


  return (
    <div className="App">


      <MyModal modalProperties={modalProperties} setModalProperties={setModalProperties}/>

      <Table data={activeNotes} extraClasses='active' 
        isCategory = {false} setModalProperties={setModalProperties} 
        loading={loadingNotes}>Active list</Table>

      <div className='buttonWrapper'>
        <MyButton onClickFunction={() => {
          setModalProperties({
            visible: true,
            modalType: modalTypes.ADD,
            index: -1,
            oldNoteContent: '',
            oldNoteCategory: Category.TASK,
          })
        }}>Add note</MyButton>
      </div>

      <Table data={archivedNotes} extraClasses='archive' 
        isCategory = {false} setModalProperties={setModalProperties}
        loading={loadingNotes}>Archived list</Table>

      <Table data={notesStats} extraClasses='summary' 
        isCategory = {true} setModalProperties={setModalProperties}
        loading={loadingNotesStats}>Summary info</Table>
    </div>
  );
}

export default App;
