import React from 'react';
import './styles/App.css';
import NotesList from './components/Table';
import { NoteService } from './services/noteService';
import { getInfoAboutCategoryes } from './services/categoryService';
import MyButton from './components/UI/MyButton/MyButton';
import MyModal from './components/UI/MyModal/MyModal';
import { modalType } from './types/modalType';

function App() {
  const activeNotes = NoteService().getActiveNotes();
  const archivedNotes = NoteService().getArchiveNotes();

  const [modal, setModal] = React.useState(false);

  return (
    <div className="App">
      <MyModal visible={modal} setVisible={setModal} method={modalType.ADD}/>
      <NotesList data={activeNotes} type='active big' isCategory = {false}>Active list</NotesList>

      <div className='buttonWrapper'>
        <MyButton onClickFunction={() => {
          setModal(true);

        }}>Add note</MyButton>
      </div>

      <NotesList data={archivedNotes} type='archive big' isCategory = {false}>Archived list</NotesList>
      <NotesList data={getInfoAboutCategoryes()} type='summary small' isCategory = {true}>Summary info</NotesList>
    </div>
  );
}

export default App;
