import { Category, CategoryType } from "../types/categoryTypes";
import { NoteType } from "../types/notesTypes";
import { NoteService } from "./noteService";

const getInfoAboutCategory = (notes: NoteType[], category: Category) => {

  const activeCount = notes.filter(note => note.category === category && !note.archived).length;
  const archivedCount = notes.filter(note => note.category === category && note.archived).length;

  return {
    id: -1,
    category: category,
    activeCount,
    archivedCount
  }
}

export const getInfoAboutCategoryes = () => {
    const notes = NoteService().getTypedNotes(NoteService().allNotes);
    const categoryes = [Category.IDEA, Category.RANDOM_THOUGHT, Category.TASK];
    let result: CategoryType[] = [];

    categoryes.forEach((category, index) => {
        result.push(getInfoAboutCategory(notes, category));
    })

    return result;
}
