import React from 'react';
import TableItem from './TableItem';
import '../styles/Table.css';
import { getNoteCells, getNoteCellsCategory } from '../utils/otherUtils';
import HeaderTable from './HeaderTable';
import { modalPropetiesType } from '../types/modalType';

interface TableProps {
  data: any[],
  type: string,
  children: string,
  isCategory: boolean,
  setModalProperties: Function,
  loading: boolean,
}

const Table: React.FC<TableProps> = ({
    data,
    children,
    type,
    isCategory,
    setModalProperties,
    loading,
  }) => {

  const loader = () => {
    return (
      <div className='loader'>
        <h2>{children}</h2>
      </div>
    )
  }

  const tableRows = () => {
    return (
      <div className='tableRows'>
        <HeaderTable isCategory={isCategory}/>
      </div>
    )
  }


  return (

    <div className='notes'>
      <h2>children</h2>
      {loading? loader() : tableRows()}
    </div>
    

  )

  

  // return (
  //   <div className='notes'>
  //       <h2>{children}</h2>

