  //           cells={isCategory? getNoteCellsCategory(row) : getNoteCells(row)} 
  //           setModalProperties={setModalProperties} addButtons={isSmall ? false : true} 
  //           idNote={row.id} key={row.id}
  //         />
  //       )}
  //   </div>
  // );
}
  
  
export default Table;
