import React from 'react';

import cl from './ButtonDiv.module.css';
import ActionLink from '../actionLink/ActionLink';
import editIcon from '../../../assets/icons/edit.png';
import archiveIcon from '../../../assets/icons/archive.png';
import unarchiveIcon from '../../../assets/icons/unarchive.png';
import deleteIcon from '../../../assets/icons/delete.png';
import { NotesActionsTypes } from '../../../types/notesTypes';
import { useDispatch } from 'react-redux';
import { useTypedSelector } from '../../../hooks/useTypeSelector';
import { modalPropetiesType, modalTypes } from '../../../types/modalType';
import { getAllNotes } from '../../../services/noteService';

interface ButtonDivProps {
  type : string,
  idNote: number,
  modalProperties: modalPropetiesType,
}

const ButtonDiv: React.FC<ButtonDivProps> = ({
  type,
  idNote,
  modalProperties,
}) => {

  const dispatch = useDispatch();
  const {notes} = useTypedSelector((state) => state.note);

  const isArchived = type.includes('archive');

  const deleteNote = () => {
    dispatch({
      type: NotesActionsTypes.DELETE_NOTE,
      payload: idNote
    })
  }

  const archiveNote = () => {
    dispatch({
      type: NotesActionsTypes.ARCHIVE_OR_UNARCHIVE_NOTE,
      payload: idNote
    })
  }

  return (
    <div className={cl.buttonDiv}>
        <ActionLink imageUrl={editIcon} alt='edit' onClickFunct={() => {
          modalProperties.setIndex(idNote);
          modalProperties.setMethod(modalTypes.EDIT);
          modalProperties.setVisible(true);

          const note = getAllNotes(notes).find(note => note.id === idNote)
          modalProperties.setOldNoteContent(note?.content);
          modalProperties.setOldNoteCategory(note?.category);
        }}/>

        {
        isArchived ? 
          <ActionLink imageUrl={unarchiveIcon} alt='unarchive' onClickFunct={archiveNote}/> :
          <ActionLink imageUrl={archiveIcon} alt='archive' onClickFunct={archiveNote}/>  
        }

        <ActionLink imageUrl={deleteIcon} alt='delete' onClickFunct={() => deleteNote()}/>
    </div>
  );
}
  
  
export default ButtonDiv;
