import React from 'react';
import './styles/App.css';
import NotesList from './components/Table';
import MyButton from './components/UI/MyButton/MyButton';
import MyModal from './components/UI/MyModal/MyModal';
import { modalPropetiesType, modalTypes } from './types/modalType';
import { getActiveNotes, getArchiveNotes } from './services/noteService';
import { useTypedSelector } from './hooks/useTypeSelector';
import { getInfoAboutCategoryes } from './services/categoryService';
import { Category } from './types/categoryTypes';

function App() {
  const {notes} = useTypedSelector(state => state.note);

  const activeNotes = getActiveNotes(notes);
  const archivedNotes = getArchiveNotes(notes);

  const [modal, setModal] = React.useState(false);
  const [index, setIndex] = React.useState(-1);
  const [modalType, setModalType] = React.useState(modalTypes.ADD);
  const [oldNoteContent, setOldNoteContent] = React.useState('');
  const [oldNoteCategory, setOldNoteCategory] = React.useState(Category.TASK);

  const modalProperties:modalPropetiesType = {
    visible: modal,
    setVisible: setModal,
    index,
    setIndex,
    method: modalType,
    setMethod: setModalType,
    oldNoteContent,
    setOldNoteContent,
    oldNoteCategory,
    setOldNoteCategory
  }

  return (
    <div className="App">
      <MyModal modalPropeties={modalProperties}/>
      <NotesList data={activeNotes} type='active big' isCategory = {false} 
        modalProperties={modalProperties}>Active list</NotesList>

      <div className='buttonWrapper'>
        <MyButton onClickFunction={() => {
          setModal(true);
          setIndex(-1);
          setModalType(modalTypes.ADD);
        }}>Add note</MyButton>
      </div>

      <NotesList data={archivedNotes} type='archive big' isCategory = {false} 
        modalProperties={modalProperties}>Archived list</NotesList>
      <NotesList data={getInfoAboutCategoryes(notes)} type='summary small' isCategory = {true}
        modalProperties={modalProperties}>Summary info</NotesList>
    </div>
  );
}

export default App;
