import React from 'react';
import {NoteType} from '../types/notesTypes'
import TableItem from './TableItem';
import '../styles/Table.css';
import { getNoteCells, getNoteCellsCategory } from '../utils/otherUtils';
import HeaderTable from './HeaderTable';

interface TableProps {
  data: any[],
  type: string,
  children: string,
  isCategory: boolean,
}

const Table: React.FC<TableProps> = ({
    data,
    children,
    type,
    isCategory
  }) => {

  const typeTable = 'blueRow ' + type;
  const isSmall = type.includes('small')

  return (
    <div className='notes'>
        <h2>{children}</h2>

        <HeaderTable type={isSmall? 'small' : 'big'} />
        {data.map((row) => 
          <TableItem typeTable={typeTable} 
          cells={isCategory? getNoteCellsCategory(row) : getNoteCells(row)} 
          addButtons={isSmall ? false : true} idNote={row.id} key={row.id}/>
        )}
    </div>
  );
}
  
  
export default Table;
