import { useDispatch } from "react-redux";
import { NoteType, NotesActionsTypes } from "../types/notesTypes";
import { useTypedSelector } from "../hooks/useTypeSelector";
import { Category } from "../types/categoryTypes";

export const noteService = () => {

  const dispatch = useDispatch();
  const {notes} = useTypedSelector(state => state.note);

  const getActiveNotes = () => {
    return getTypedNotes(notes).filter(note => !note.archived);
  }

  const getArchiveNotes = () => {
    return getTypedNotes(notes).filter(note => note.archived);
  }

  const getTypedNotes = (notes: NoteType[]) => {
    return notes;
  }

  const addNote = (noteContent:string, noteCategory:string) => {
    const year = new Date().getFullYear();
    const month = new Date().getMonth() + 1;
    const day = new Date().getDate();

    dispatch({
      type: NotesActionsTypes.ADD_NOTE,
      payload: { 
          id: Math.max(...getTypedNotes(notes).map(note => note.id)) + 1,
          createdTime: `${year}-${month}-${day}`,
          content: noteContent,
          category: noteCategory,
          archived: false,
      }
    });
  }

  const removeNote = (idNote: number) => {
    dispatch({
      type: NotesActionsTypes.DELETE_NOTE,
      payload: idNote
    })
  }

  const archiveNote = (idNote: number) => {
    dispatch({
      type: NotesActionsTypes.ARCHIVE_OR_UNARCHIVE_NOTE,
      payload: idNote
    })
  }

  const editNote = (note: NoteType) => {
    // dispatch({

    // });
  }

  return {
    notes,
    getActiveNotes,
    getArchiveNotes,
    getTypedNotes,
    addNote,
    removeNote,
    archiveNote,
    editNote,
  };
}
