import React, { useEffect } from 'react';
import './styles/App.css';
import Table from './components/Table';
import MyButton from './components/UI/MyButton/MyButton';
import MyModal from './components/UI/MyModal/MyModal';
import { modalPropetiesType, modalTypes } from './types/modalType';
import { getActiveNotes, getArchiveNotes } from './services/noteService';
import { useTypedSelector } from './hooks/useTypeSelector';
import { getInfoAboutCategoryes } from './services/categoryService';
import { Category } from './types/categoryTypes';
import { useActions } from './hooks/useActions';



function App() {
  const {notes, loading} = useTypedSelector(state => state.note);
  const activeNotes = getActiveNotes(notes);
  const archivedNotes = getArchiveNotes(notes);

  const {fetchNotes, getNote} = useActions();

  useEffect(() => {
    fetchNotes();
    console.log(getNote(2));
  }, [])


  const [modalProperties, setModalProperties] = React.useState({
    visible: false,
    modalType: modalTypes.ADD,

    index: -1,
    oldNoteContent: '',
    oldNoteCategory: Category.TASK,
  })


  return (
    <div className="App">


      <MyModal modalProperties={modalProperties} setModalProperties={setModalProperties}/>
      <Table data={activeNotes} type='active big' 
        isCategory = {false} setModalProperties={setModalProperties} 
        loading={loading}>Active list</Table>

      <div className='buttonWrapper'>
        <MyButton onClickFunction={() => {
          setModalProperties({
            visible: true,
            modalType: modalTypes.ADD,
            index: -1,
            oldNoteContent: '',
            oldNoteCategory: Category.TASK,
          })
        }}>Add note</MyButton>
      </div>

      <Table data={archivedNotes} type='archive big' 
        isCategory = {false} setModalProperties={setModalProperties}
        loading={loading}>Archived list</Table>

      <Table data={getInfoAboutCategoryes(notes)} type='summary small' 
        isCategory = {true} setModalProperties={setModalProperties}
        loading={loading}>Summary info</Table>
    </div>
  );
}

export default App;
