import React from 'react';

import TableItemCell from './TableItemCell';
import { isBig } from '../utils/otherUtils';
import ButtonDiv from './UI/ButtonDiv/ButtonDiv';

interface TableItemProps {
  typeTable: string,
  cells: any[],
  addButtons? : boolean,
  idNote?: number
}

const TableItem: React.FC<TableItemProps> = ({
  typeTable,
  cells,
  addButtons,
  idNote
}) => {

  const cl = 'notesTableRow ' + typeTable;
  const addBigItem = cells.length > 3;

  return (
    <div className={cl}>
        {cells.map((cell, index) => 
          addBigItem ? 
          <TableItemCell haveBigItem = {isBig(index)}>{cell}</TableItemCell> :
          <TableItemCell>{cell}</TableItemCell>
        )}
        
        {addButtons ? <ButtonDiv idNote={idNote} type={typeTable}/> : ''}
    </div>
  );

}
  
  
export default TableItem;
