import React from 'react';
import TableItem from './TableItem';
import '../styles/Table.css';
import { getNoteCells, getNoteCellsCategory } from '../utils/otherUtils';
import HeaderTable from './HeaderTable';
import { modalPropetiesType } from '../types/modalType';

interface TableProps {
  data: any[],
  type: string,
  children: string,
  isCategory: boolean,
  modalProperties: modalPropetiesType
  setModalProperties: Function,
  loading: boolean,
}

const Table: React.FC<TableProps> = ({
    data,
    children,
    type,
    isCategory,
    modalProperties,
    setModalProperties,
    loading,
  }) => {

  const typeTable = 'blueRow ' + type;
  const isSmall = type.includes('small')

  if (loading) {
    return (
      <div className='loader'>
        <h1>loading...</h1>
      </div>
    )
  }

  return (
    <div className='notes'>
        <h2>{children}</h2>

        <HeaderTable type={isSmall? 'small' : 'big'} />
        {data.map((row) => 
          <TableItem typeTable={typeTable} 
            cells={isCategory? getNoteCellsCategory(row) : getNoteCells(row)} 
            setModalProperties={setModalProperties} addButtons={isSmall ? false : true} 
            idNote={row.id} key={row.id}
          />
        )}
    </div>
  );
}
  
  
export default Table;
